Child(document.createElement('br'));					  var text = document.createElement('text');					  text.textContent = address					  infowincontent.appendChild(text);					  var icon = customLabel[type] || {};					  var marker = new google.maps.Marker({						map: map,						position: point,						label: icon.label					  });					  marker.addListener('click', function() {						infoWindow.setContent(infowincontent);						infoWindow.open(map, marker);					  });					});				  });				}			  function downloadUrl(url, callback) {				var request = window.ActiveXObject ?					new ActiveXObject('Microsoft.XMLHTTP') :					new XMLHttpRequest;				request.onreadystatechange = function() {				  if (request.readyState == 4) {					request.onreadystatechange = doNothing;					callback(request, request.status);				  }				};				request.open('GET', url, true);				request.send(null);			  }			  function doNothing() {}			</script>	<script async defer src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBcnKNfwtwd9TqfsdxmVj9Kipq7t4xKyuY&callback=initMap">    <!-- jQuery -->    <script src="../bower_components/jquery/dist/jquery.min.js"></script>    <!-- Bootstrap Core JavaScript -->    <script src="../bower_components/bootstrap/dist/js/bootstrap.min.js"></script>    <!-- Metis Menu Plugin JavaScript -->    <script src="../bower_components/metisMenu/dist/metisMenu.min.js"></script>    <!-- Morris Charts JavaScript -->    <script src="../bower_components/raphael/raphael-min.js"></script>    <script src="../bower_components/morrisjs/morris.min.js"></script>    <script src="../js/morris-data.js"></script>    <!-- Custom Theme JavaScript -->    <script src="../dist/js/sb-admin-2.js"></script></body></html>